// Returns true if key is present in given BST 
boolean search(Node root, int key) 
{ 
    // Base Case 
    if (root == null) 
        return false; 

    // If key is present at root 
    if (root.key == key) 
        return true; 

    // Recursive calls for non-empty subtrees 
    boolean left = search(root.left, key); 
    boolean right = search(root.right, key); 

    return left || right; 
}

