    links = re.findall(r'<a .*?href=[\'"](.*?)[\'"].*?>', content)

    for i in links:
        if i.startswith("/"):
            base_url = url
            i = base_url + i
            if i in urls:
                continue
            urls.add(i)
            get_urls(i)
        elif url in i:
            if i in urls:
                continue
            urls.add(i)
            get_urls(i)

