function findPerfectNumbers(n) {
  var perfects = [];

  for(var i = 1; i<=n; i++){
    var divisorsSum = 0;
    for(var j = 1; j<i; j++){
      if(i % j == 0){
        divisorsSum += j;
      }
    }
    if(divisorsSum == i){
      perfects.push(i);
    }
  }

  return perfects;
}

