function findPrimeFactors(num) { 
    let factors = []; 
    while (num % 2 === 0) { 
        factors.push(2); 
        num = num / 2; 
    } 

    // num must be odd at this point 
    for (let i = 3; i <= Math.sqrt(num); i += 2) { 
        while (num % i === 0) { 
            factors.push(i); 
            num = num / i; 
        } 
    } 

    // This condition is to handle the case 
    // when num is a prime number greater than 2 
    if (num > 2) 
        factors.push(num); 

    // Distinct prime factors
    let uniqFactors = new Set(factors);
    return [...uniqFactors];
}

