//Function to print all the permutations of the given string
void printPermutations(char *str, int l, int r) {

    if (l == r)
      printf("%s\n", str);
    else
    {
        for (int i = l; i <= r; i++)
        {
            //swap the characters at positions l and i
            char temp = str[l];
            str[l] = str[i];
            str[i] = temp;

            //Recursively call the function to generate all permutations
            printPermutations(str, l+1, r);

            //Again swap the characters to their original positions
            temp = str[l];
            str[l] = str[i];
            str[i] = temp; 
        } 
    }
}

