    int n = arr.length; 
    int[] temp = new int[n]; 

    int j = 0; 
    for (int i=0; i<n-1; i++){ 
        if (arr[i] != arr[i+1]) 
            temp[j++] = arr[i]; 
    } 

    temp[j++] = arr[n-1];     

    int[] res = new int[j];   
    for (int i=0; i<j; i++){ 
        res[i] = temp[i]; 
    } 

    return res; 
