def permute(lis):
    if len(lis) == 0:
        return [] 
    if len(lis) == 1:
        return [lis] 

    l = [] 
    for i in range(len(lis)):
        m = lis[i] 

        remLis = lis[:i] + lis[i+1:] 

        for p in permute(remLis): 
            l.append([m] + p) 
    return l 

