function Fibonacci($n)
{
    if ($n == 0)
        return '0';
    else if ($n == 1)
        return '1';
    else
    {
        $fibonaccinumbers[0] = 0;
        $fibonaccinumbers[1] = 1;
        for ($i = 2; $i <= $n; $i++)
        {
            $fibonaccinumbers[$i] = $fibonaccinumbers[$i - 1] +
                                    $fibonaccinumbers[$i - 2];
        }

        return $fibonaccinumbers[$n];
    }
}

