int fibonacci_sequence(int num_terms)  
{  
    int first = 0, second = 1, next = 0;  

    if (num_terms == 0)  
    {  
        return 0;  
    }  
    else if (num_terms == 1)  
    {  
        return 1;  
    }  
    else
    {  
        for (int i = 2; i <= num_terms; i++)  
        {  
            next = first + second;  
            first = second;  
            second = next;  
        }  
    }  
    return next;  
}  

